CREATE TABLE IF NOT EXISTS `artikel_user` (
  `id_artikel_user` int(11) NOT NULL AUTO_INCREMENT,
  `judul` varchar(50) NOT NULL,
  `isi` text NOT NULL,
  `gambar` varchar(100) NOT NULL,
  `tanggal` date NOT NULL,
  `status` enum('Diterima','Ditolak','Pending') NOT NULL,
  `id_user` int(11) NOT NULL,
  PRIMARY KEY (`id_artikel_user`),
  KEY `id_user` (`id_user`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=37 ;

